import { Component } from '@angular/core';
import { WebsocketService } from './service/websocket.service';
import { CountService } from './service/count.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'dome_Front';
  sock = new WebSocket('ws://127.0.0.1:8000');

  constructor(
    private ws: WebsocketService,
    private ct: CountService,
    ){}
  async ngOninit():Promise<void> {
    await this.sock.addEventListener('open', (e) => {
      console.log('Socket 接続成功');
    });
    await this.sock.addEventListener('message', (e) => {// サーバーからデータを受け取る
      console.log(e.data);
    });
    this.sock.send('hello from angular');
    // this.ws.connect()
    // .subscribe((data:any) => {console.log(data)});
  }

  async countUp(num: number): Promise<void>{
    const result = await this.ct.postCount(num);
    result.subscribe((data:any) => {
      console.log(data);
    })
  }
}
